package com.kodnest.pattern;

public class pattern10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=3;i++) {
			for(int j=1;j<=i;j++) {
				System.out.print("*");
			}
			System.out.println();
		}
		for(int k=3;k<=4;k++) {
			for(int a=1;a<=3;a++) {
				System.out.print("*");
			}
			System.out.println();
		}
		for(int b=1;b<=3;b++) {
			for(int c=1;c<=b;c++) {
				System.out.print("*");
			}
			System.out.println();
		}

	}

}
