package com.kodnest.pattern;

public class pattern7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<5;i++) {
			
			for(int j=1;j<5;j++) {
				
				System.out.print("*");
				
			}System.out.println();
		}
		for(int k=1;k<5;k++) {
			for(int x=1;x<5;x++) {
				
			System.out.print("$");
		}

	System.out.println();
		}

}}
