package com.kodnest.pattern;

public class pattern9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=4;i<=6;i++) {
			for(int j=1;j<=4;j++) {
				System.out.print("*");
			}
			System.out.println();
		}
		for(int k=1;k<=4;k++) {
			for(int a=1;a<=k;a++) {
				System.out.print("*");
			}
			System.out.println();
		}
	}}


